import { Transition, animated,config } from 'react-spring'
import React, { Component } from 'react'
import Textani from './Textani'
import "./stylee.css"

export default class Toggle extends Component {
    constructor() {
        super()

        this.state = {
          toggle: false,
        }
      }

      render() {

        const { toggle } = this.state
        return (
            <div>

          <Transition
            items={toggle}
            from={{ opacity: 0 }}
            enter={{ opacity: 1 }}
            leave={{ opacity: 0 }}
            reverse={toggle}
            delay={200}
            config={config.molasses}
            onRest={() =>
                this.setState({
                    toggle: !toggle,
                })
            }>
            {({ opacity }, item) =>
              item ? (
                  <animated.div
                  style={{
                      position: 'absolute',
                      opacity: opacity.to({ range: [0.0, 1.0], output: [0, 1] }),
                    }}>
                  <div className="hrflex">
              <div className="crdteam">
                <div className="igg1" />
                <h4 className="crdtext crdtext1">J Ashwin Nair</h4>
                {/* <h4 className="crdtext crdtext2">Mentor</h4> */}
              </div>
              <div className="crdteam">
                <div className="igg2" />
                <h4 className="crdtext crdtext1">Garima Patel</h4>
                {/* <h4 className="crdtext crdtext2">Mentor</h4> */}
              </div>
           </div>
                </animated.div>
              ) : (
                  <animated.div
                  style={{
                      position: 'absolute',
                      opacity: opacity.to({ range: [1.0, 0.0], output: [0, 1] }),
                    }}>
                  ðŸ¤ª
                </animated.div>
              )
            }
          </Transition>
            </div>
        )
      }
}
