    constructor() {
        super()

        this.state = {
          toggle: false,
        }
      }

      render() {

        const { toggle } = this.state
        return (
          <Transition
            items={toggle}
            from={{ opacity: 0 }}
            enter={{ opacity: 1 }}
            leave={{ opacity: 0 }}
